    private void OkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OkActionPerformed
        String mat = materia.getText();
        String day = dia.getText();
        String hour = hora.getText();
        String b = bloque.getText();
        int bloque = Integer.valueOf(b);
        String a = aula.getText();
        int aula = Integer.valueOf(a);
        String af = aforo.getText();
        int aforo = Integer.valueOf(af);
        String numEs = numEstudiantes.getText();
        int numEstudiantes = Integer.valueOf(numEs);
        String numLis = listasnum.getText();
        int numListas = Integer.valueOf(numLis);
        String nombreEstudiantes = listaEstudiantes.getText();
        
        
        Procesamiento.asignarAsistencia();
        Page2 p2 = new Page2();
        p2.setVisible(true);
        p2.materia = mat;
        p2.hora = hour;
        p2.dia = day;
        p2.aula= b+"-"+a;
    }//GEN-LAST:event_OkActionPerformed

