    int i = 0;
    
    private void registrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_registrarActionPerformed
        String mat = materia.getText();
        String day = dia.getText();
        String hour = hora.getText();
        String b = bloque.getText();
        int bloque = Integer.valueOf(b);
        String a = aula.getText();
        int aula = Integer.valueOf(a);
        String af = aforo.getText();
        int aforo = Integer.valueOf(af);
        String numEs = numEstudiantes.getText();
        int numEstudiantes = Integer.valueOf(numEs);
        String numLis = listasnum.getText();
        int numListas = Integer.valueOf(numLis);
      
        if(i == 0){
        Captura.ingresoDatos(mat, day, hour, bloque, aula, aforo, numEstudiantes, numListas);
        }
        if(i<numEstudiantes){
            Captura.estudiantes[i] = listaEstudiantes.getText();
            
        } else {
            javax.swing.JOptionPane.showMessageDialog(this, "Solo puede ingresar "+(numEstudiantes)+" estudiantes.");
        }
       i++;
       listaEstudiantes.setText("");
        
    }//GEN-LAST:event_registrarActionPerformed

